select 'delete_device' gubun, clientgrp_idx, client_name, ip_addr, mac_addr, os_type, delete_date stats_date from CLIENT_MAST where delete_date > #{start_date,jdbcType=VARCHAR} and delete_date < #{end_date,jdbcType=VARCHAR} and invalid_flag = 1 and clientgrp_idx in
